from PIL import Image
image = Image.open("challenge_alpha.jpg")
array = image.load()
width, height = image.size

content = ''
for i in range(width):
    c = array[i, 0]
    if c == 255:
        break
    # print(c)
    if c > 99:
        content = content + '0'
    else:
        content = content + '1'

# 
# 

cipher = ''
for i in range(int(len(content) / 8)):
    c = content[i * 8:(i + 1) * 8]
    cipher = cipher + chr(int(c, 2))
print(cipher)

